import axios from "axios";

export const getAllPosts = async () => {
  try {
    const option = {
      method: "get",
      url: "/api/v1/posts",
    };
    const response = await axios(option);
    const posts = response.data.data.posts;
    // console.log(posts);
    return posts;
  } catch (error) {
    console.log(error);
  }
};

