import axios from "axios";

export const getAllPosts = async () => {
  try {
    const option = {
      method: "get",
      url: "/api/v1/posts",
    };
    const response = await axios(option);
    const posts = response.data.data.posts;
    // console.log(posts);
    return posts;
  } catch (error) {
    console.log(error);
  }
};

export const createOnePost = async (postInput) => {
  try {
    const option = {
      method: "post",
      url: "/api/v1/posts",
      data: postInput
    }
    const response = await axios(option);
    console.log(response.data);
  } catch (error) {

  }
}
