import axios from "axios";

export const postCurrentUser = async (e, userInput) => {
  try {
    e.preventDefault();
    const option = {
      method: "post",
      url: "/api/v1/auth/login",
      data: userInput,
    };
    const response = await axios.post(option);
    console.log("userInput", userInput);
    console.log(response);
  } catch (error) {
    console.log(error);
  }
};
